ch.';    $_POST['pass1'] = '';    $_POST['pass2'] = '';    $errored = true;  }  $_POST['email'] = trim($_POST['email']);  $_POST['email2'] = trim($_POST['email2']);  if(strlen($_POST["email"]) == 0)  {    $email_message = "You must have an e-mail address to confirm your account.";    $_POST["email"] = "";    $errored = true;  }  else if($_POST["email"] != $_POST["email2"])  {    $email_message = "Your e-mail addresses did not match.";    $_POST['email'] = '';    $_POST['email2'] = '';    $errored = true;  }  else if(strpos($_POST['email'], '@psypets.net'))  {    $email_message = 'That e-mail address is already in use.';    $errored = true;  }  else  {    $command = 'SELECT email FROM monster_users ' .               'WHERE email=' . quote_smart($_POST["email"]) . " OR newemail=" . quote_smart($_POST["email"]) . " LIMIT 1";    $matches = $database->FetchSingle($command, 'signup.php');    if($matches !== false)    {      $email_message = 'That e-mail address is already in use.';      $errored = true;    }  }  $_POST['display'] = trim($_POST['display']);  if(strlen($_POST['display']) < 2 || strlen($_POST['display']) > 24)  {    $display_message = 'Your resident name must be between 2 and 24 characters.';    $errored = true;  }  else if(preg_match("/[^a-zA-Z0-9_ .!?~'-]/", $_POST["display"]))  {    $display_message = "Please only use alphanumeric characters (or some punctuation, for example spaces are allowed)";    $errored = true;  }  else if(preg_match("/[^a-zA-Z]/", $_POST['display']{0}))  {    $display_message = "Your resident name must start with a letter.";    $errored = true;  }  else  {    $matches = $database->FetchSingle('      SELECT idnum      FROM monster_users      WHERE display_normalized=' . quote_smart(normalized_display_name($_POST['display'])) . '      LIMIT 1    ');    if($matches !== false)    {      $display_message = "That resident name is already in use.  Sorry :(";      $errored